import React from "react"
import ReactDOM from "react-dom"
import DisplayInfo from "./DisplayInfo"

class GeneralInfo extends React.Component {
    constructor() {
        super();

        this.state = {
            info: {
                name: '',
                phone: '',
                email: '',
            },
        }
    }

    getName = (e) => {
        this.setState({
            info : {
                name: e.target.value,
            }
        });
    };

    getPhone = (e) => {
        this.setState({
            info : {
                phone: e.target.value,
            }
        });
    };

    getEmail = (e) => {
        this.setState({
            info : {
                email: e.target.value
            }
        });
    };

    onSubmitInfo = (e) => {
        e.preventDefault();
        this.setState({
            name: { text: '' },
            phone: { text: '' },
            email: { text: '' },
        });
    };

       render () {
            const { name, phone, email } = this.state;

           return ( 
               <div>
                   <h2>General Info</h2>
                   <form onSubmit={this.onSubmitInfo}>
                       <label htmlFor="name">Name</label>
                           <input 
                                onChange={this.getName}
                                value={name.text}
                                type="text" 
                                id="name">
                            </input>
                           <br></br>
                        <label htmlFor="phone">Phone number</label>
                            <input 
                                onChange={this.getPhone}
                                value={phone.text}
                                type="tel" 
                                id="phone">
                            </input>
                            <br></br>
                        <label htmlFor="email">Email</label>
                            <input 
                                onChange={this.getEmail}
                                value={email.text}
                                type="email" 
                                id="email">
                                </input>
                            <br></br>
                        <button type="submit">
                            Submit General Info
                        </button>
                        <button type="submit">
                            Edit General Info
                        </button>
                   </form>

                    <DisplayInfo info={name} />

               </div>
           )
       }
}

export default GeneralInfo;
