import React from 'react'
import ReactDOM from 'react-dom'

class Education extends React.Component {
    constructor (){
        super()

        this.state = {
            school: { text: '' },
            title: { text: '' },
            startDate: { text: '' },
            endDate: { text: '' },
        };
    }

    getSchool = (e) => {
        this.setState({
            task : {
                text: e.target.value,
            }
        });
    };

    getTitle = (e) => {
        this.setState({
            title: {
                text: e.target.value,
            }
        });
    };

    getStartDate = (e) => {
        this.setState({
            startDate: {
                text: e.target.value,
            }
        });
    };

    getEndDate = (e) => {
        this.setState({
            endDate: {
                text: e.target.value,
            }
        });
    };

    onSubmitTask = (e) => {
        e.preventDefault();
        this.setState({
            school: { text: '' },
            title: { text: '' },
            startDate: { text: '' },
            endDate: { text: '' },
        });
    };

    render() {
        const { school, title, startDate, endDate } = this.state;

        return (
            <div>
                <h2>Educational experience</h2>
                <form>
                    <label htmlFor="schoolName">School Name</label>
                        <input type="text" id="schoolName"></input>
                        <br></br>
                    <label htmlFor="title">Title of Study</label>
                        <input type="text" id="title"></input>
                        <br></br>
                    <label htmlFor="startDateOfStudy">Start date of study</label>
                        <input type="date" id="dateOfStudy"></input>
                        <br></br>
                    <label htmlFor="endDate">End date of working</label>
                        <input type="date" id="endDate"></input>
                        <br></br>
                    <button type="submit">
                        Submit Educational experience
                    </button>
                </form>
            </div>
        )
    }
}

export default Education;
