import React from 'react'
import DisplayEducation from './DisplayEducation';

class Education extends React.Component {
    constructor (){
        super()

        this.state = {
            education: {
                school: '',
                title: '',
                startDate: '',
                endDate: '',
            },

        };
    }

    getSchool = (e) => {
        this.setState({
            education: {
                school: e.target.value,
            }
        });
    };

    getTitle = (e) => {
        this.setState({
            education: {
                title: e.target.value,
            }
        });
    };

    getStartDate = (e) => {
        this.setState({
            education: {
                startDate: e.target.value
            }
        });
    };

    getEndDate = (e) => {
        this.setState({
            education: {
                endDate: e.target.value,
            }
        });
    };

    onSubmitTask = (e) => {
        e.preventDefalit();
        this.setState({
            education: {
                school: '',
                title: '',
                startDate: '',
                endDate: '',
            },
        });
    };

    render() {
        const { education } = this.state;

        return (
            <div>
                <h2>Educational experience</h2>
                <form onSubmit={this.onSubmitTask}>
                    <label htmlFor="schoolName">School Name</label>
                        <input 
                            onChange={this.getSchool}
                            value={education.school.text}
                            type="text" 
                            id="schoolName">
                        </input>
                        <br></br>
                    <label htmlFor="title">Title of Study</label>
                        <input 
                            onChange={this.getTitle}
                            value={education.title.text}
                            type="text" 
                            id="title">
                        </input>
                        <br></br>
                    <label htmlFor="startDateOfStudy">Start date of study</label>
                        <input
                            onChange={this.getStartDate}
                            value={education.startDate.text}
                            type="date" 
                            id="dateOfStudy">
                            </input>
                        <br></br>
                    <label htmlFor="endDate">End date of working</label>
                        <input 
                            onChange={this.getEndDate}
                            value={education.endDate.text}
                            type="date" 
                            id="endDate">
                            </input>
                        <br></br>
                    <button type="submit">
                        Submit Educational experience
                    </button>
                </form>
                <DisplayEducation education={education} />
            </div>
        )
    }
}

export default Education;
