import React from 'react'


class Education extends React.Component {
    constructor (){
        super()

        this.state = {
            school: { text: '' },
            title: { text: '' },
            startDate: { text: '' },
            endDate: { text: '' },
        };
    }

    getSchool = (e) => {
        this.setState({
             school: {
                text: e.target.value,
            }
        });
    };

    getTitle = (e) => {
        this.setState({
            title: {
                text: e.target.value,
            }
        });
    };

    getStartDate = (e) => {
        this.setState({
            startDate: {
                text: e.target.value,
            }
        });
    };

    getEndDate = (e) => {
        this.setState({
            endDate: {
                text: e.target.value,
            }
        });
    };

    onSubmitTask = (e) => {
        e.preventDefault();
        this.setState({
            school: { text: '' },
            title: { text: '' },
            startDate: { text: '' },
            endDate: { text: '' },
        });
    };

    render() {
        const { school, title, startDate, endDate } = this.state;

        return (
            <div>
                <h2>Educational experience</h2>
                <form onSubmit={this.onSubmitTask}>
                    <label htmlFor="schoolName">School Name</label>
                        <input 
                            onChange={this.getSchool}
                            value={school.text}
                            type="text" 
                            id="schoolName">
                        </input>
                        <br></br>
                    <label htmlFor="title">Title of Study</label>
                        <input 
                            onChange={this.getTitle}
                            value={title.text}
                            type="text" 
                            id="title">
                        </input>
                        <br></br>
                    <label htmlFor="startDateOfStudy">Start date of study</label>
                        <input
                            onChange={this.getStartDate}
                            value={startDate.text}
                            type="date" 
                            id="dateOfStudy">
                            </input>
                        <br></br>
                    <label htmlFor="endDate">End date of working</label>
                        <input 
                            onChange={this.getEndDate}
                            value={endDate.text}
                            type="date" 
                            id="endDate">
                            </input>
                        <br></br>
                    <button type="submit">
                        Submit Educational experience
                    </button>
                    <button type="submit">
                        Edit Educational experience
                    </button>
                </form>
            </div>
        )
    }
}

export default Education;
